// backspace suggestList.flush() break case 13: // Enter evt.preventDefault() if (!suggestList.hasChildNodes()) { return } if (!activeLi) { return } else { plzInput.value = activeLi.textContent.substr(0,5) ortInput.value = activeLi.textContent.substr(6) suggestList.flush() ortInput.focus() } break case 37: break case 38: // cursor up if (!suggestList.hasChildNodes()) { return } for (let item of Array.from(suggestList.querySelectorAll('li'))) { if (item!== activeLi) { item.classList.remove('active') } } if (activeLi) { activeLi.classList.remove('active') let prevLi = activeLi.previousSibling || suggestList.querySelector('li:last-of-type') if (prevLi) { prevLi.classList.add('active') if (prevLi.offsetTop < suggestList.scrollTop || prevLi.offsetTop > suggestList.scrollTop + suggestList.getBoundingClientRect().height) { suggestList.scrollTop = prevLi.offsetTop } } } break case 39: break case 40: // cursor down if (!suggestList.hasChildNodes()) { return } for (let item of Array.from(suggestList.querySelectorAll('li'))) { if (item!== activeLi) { item.classList.remove('active') } } if (activeLi) { activeLi.classList.remove('active') let nextLi = activeLi.nextSibling || suggestList.querySelector('li:first-of-type') if (nextLi) { activeLi.classList.remove('active') nextLi.classList.add('active') if (nextLi.offsetTop < suggestList.scrollTop || nextLi.offsetTop > suggestList.scrollTop + suggestList.getBoundingClientRect().height - 32) { suggestList.scrollTop = nextLi.offsetTop - suggestList.getBoundingClientRect().height + 32 } } } else { suggestList.querySelector('li:first-of-type').classList.add('active') } break case 46: // delete suggestList.flush() break default: break } } } }) plzInput.addEventListener('blur', function(evt) { setTimeout(function() { suggestList.flush() }, 200500) }) ortInput.addEventListener('input', function(evt) { console.log(this.value) }) } } plzOrtAutoComplete(); .autocomplete-suggest { background-color: #fff; border: 1px solid #ddd; box-shadow: 3px